    public static final String generateRandomBuzWords(int... n) throws CryptoMessageException, InvalidParameterException {
        final int wArrLen = WORDS.length;
        try {
            final int len;
            switch (n.length) {
                case 0:
                    len = 3;
                    break;
                case 1:
                    len = n[0];
                    break;
                default:
                    throw new InvalidParameterException("exactly 0 or 1 argument required, given " + Arrays.toString(n));
            }
            SecureRandom sr = SecureRandom.getInstanceStrong();

            String buzWords = IntStream
                    .range(0, len)
                    .parallel()
                    .mapToObj((index) -> {
                        return WORDS[sr.nextInt(0, wArrLen)];
                    }).collect(Collectors.joining(" "));
            return buzWords;
        } catch (NoSuchAlgorithmException ex) {
            throw new CryptoMessageException(ex);
        }

    }
